import React, {useState, useEffect} from 'react';
import { Canvas } from '@react-three/fiber'

function App() {
  const [camPos, setCamPos] = useState([19, -5, 6])
  const [camRot, setCamRot] = useState([1.6, 0, 0])


  useEffect(() => {
    const timer = setTimeout(() => {
      setCamPos(prev => [prev[0] + 1, prev[1], prev[2]])
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div id="canvas-container" style={{ width: "100vw", height: "100vh" }}>
      <Canvas camera={{fov: 75, near: 0.1, far: 1000, position: camPos, rotation: camRot}}>
        <mesh>
          <torusGeometry args={[20, 7, 2, 100]} />
          <meshStandardMaterial />
        </mesh>
        <mesh>
          <coneGeometry args={[1, 5, 32]} />
          <meshStandardMaterial color='#ffff00' />
        </mesh>
      </Canvas>
    </div>
  );
}

export default App;
